var path=require('path');
//object
var search={}
//props
search.params=['mobile','retina','tablet','desktop','wide'];
//methods
search.validate=function(item){
	if( typeof item ==='object' && typeof item.display !=='undefined'){
		return true;
	}else{
		return false;
	}
}	
search.getDisplay=function(obj,res){
	if(search.validate(obj)){
		var len=search.params.length;
		var valid=false;
		for(i=0;i<len;i++){
			if(obj.display === search.params[i]){
				valid=true;
			}
		}
		if(valid){
			res.sendFile(path.join(__dirname,'../images/'+obj.display+'.jpg'));
		}else{
			res.send('not in range');
		}	
	}else{
		res.send('invalid name param: must be display')
	}
}
module.exports=search;